int baseBigMap[BIGMAP_Y][BIGMAP_X];

int map[MAP_Y][MAP_X];

//宣言と初期化
int bigPosY = PLAYER_Y;
int bigPosX = PLAYER_X;
int scroll_Y = 0;//スクロールのためのずらす数y軸
int scroll_X = 0;//スクロールのためのずらす数x軸

int prevBigPosY = PLAYER_Y;
int prevBigPosX = PLAYER_X;

char inputKey = '\0';

//==================================================
//main関数（エントリーポイント）
//==================================================
int main(void)
{
	//初回ベースマップの設定
	for (int y = 0; y < BIGMAP_Y; y++)
	{
		for (int x = 0; x < BIGMAP_X; x++)
		{
			baseBigMap[y][x] = bigMap[y][x];
		}
	}

	for (;;)
	{
		movePlayer();

		obstacleDetection();

		mapScroll();

		initializeMap();

		//移動後プレイヤー位置設定
		map[bigPosY - scroll_Y][bigPosX - scroll_X] = 99;

		//消して描画
		system("cls");
		drowMap();

		//デバッグ用テキスト
		std::cout << "bigPosX,Y " << bigPosX << " / " << bigPosY;
		std::cout << "\nscroll_X,Y " << scroll_X << " / " << scroll_Y;

		//キー入力受付
		inputKey = _getch();
	}
}

//==================================================
//マップ描画関数
//==================================================
void drowMap()
{
	//マップの描画
	for (int y = 0; y < MAP_Y; y++)
	{
		for (int x = 0; x < MAP_X; x++)
		{
			switch (map[y][x])
			{
			case 1:
				std::cout << '*';
				break;
			case 2:
				std::cout << '-';
				break;
			case 3:
				std::cout << '|';
				break;
			case 4:
				std::cout << '=';
				break;
			case 50:
				std::cout << 'S';
				break;
			case 99:
				std::cout << 'P';
				break;
			default:
				std::cout << ' ';
				break;
			}
		}

		std::cout << "\n";
	}
}

//==================================================
//プレイヤー移動関数
//==================================================
void movePlayer()
{
	//１つ前のプレイヤー位置保持
	prevBigPosY = bigPosY;
	prevBigPosX = bigPosX;

	//入力判定
	if (inputKey == 'w')
		bigPosY -= 1;
	if (inputKey == 's')
		bigPosY += 1;
	if (inputKey == 'a')
		bigPosX -= 1;
	if (inputKey == 'd')
		bigPosX += 1;
}

//==================================================
//障害物判定関数
//==================================================
void obstacleDetection()
{
	switch (baseBigMap[bigPosY][bigPosX])
	{
	case 1:
		bigPosY = prevBigPosY;
		bigPosX = prevBigPosX;
		break;
	case 2:
		bigPosY = prevBigPosY;
		bigPosX = prevBigPosX;
		break;
	case 3:
		bigPosY = prevBigPosY;
		bigPosX = prevBigPosX;
		break;
	default:
		break;
	}

}

//==================================================
//マップスクロール関数
//==================================================
void mapScroll()
{
	//Y・縦

	//プレイヤー中心に来るまで
	if (bigPosY <= SCROLLSTOP_Y)
	{
		scroll_Y = 0;
	}
	//プレイヤー中心
	else if (bigPosY > SCROLLSTOP_Y && bigPosY - SCROLLSTOP_Y < BIGMAP_Y - MAP_Y)
	{
		scroll_Y = bigPosY - SCROLLSTOP_Y;
	}
	//プレイヤー下端に到達
	else
	{
		scroll_Y = BIGMAP_Y - MAP_Y;
	}

	//X・横

	//プレイヤー中心に来るまで
	if (bigPosX <= SCROLLSTOP_X)
	{
		scroll_X = 0;
	}
	//プレイヤー中心
	else if (bigPosX > SCROLLSTOP_X && bigPosX - SCROLLSTOP_X < BIGMAP_X - MAP_X)
	{
		scroll_X = bigPosX - SCROLLSTOP_X;
	}
	//プレイヤー右端に到達
	else
	{
		scroll_X = BIGMAP_X - MAP_X;
	}
}

//==================================================
//マップ初期化関数
//==================================================
void initializeMap(void)
{
	//描画マップ設定
	for (int y = 0; y < MAP_Y; y++)
	{
		for (int x = 0; x < MAP_X; x++)
		{
			map[y][x] = baseBigMap[scroll_Y + y][scroll_X + x];
		}
	}
}

