int baseBigMap[BIGMAP_Y][BIGMAP_X];

int map[MAP_Y][MAP_X];

//宣言と初期化
int posY = PLAYER_Y;
int posX = PLAYER_X;
int scroll_Y = 0;//スクロールのためのずらす数y軸
int scroll_X = 0;//スクロールのためのずらす数x軸

int prevPosY = PLAYER_Y;
int prevPosX = PLAYER_X;

char inputKey = '\0';

//==================================================
//main関数（エントリーポイント）
//==================================================
int main(void)
{
	for (;;)
	{		
		//中心に来るまで
		if (posY < SCROLLSTOP_Y)
		{
			scroll_Y = 0;
		}
		//中心に来た
		else if (posY >= SCROLLSTOP_Y && posY - SCROLLSTOP_Y < MAP_Y)
		{
			scroll_Y = posY - SCROLLSTOP_Y;
		}
		//下まで来た
		else
		{
			scroll_Y = MAP_Y;
		}


		//中心に来るまで
		if (posX < SCROLLSTOP_X)
		{
			scroll_X = 0;
		}
		//中心に来た
		else if (posX >= SCROLLSTOP_X && posX - SCROLLSTOP_X <= MAP_X)
		{
			scroll_X = posX - SCROLLSTOP_X;
		}
		//下まで来た
		else
		{
			scroll_X = MAP_X;
		}

		for (int y = 0; y < BIGMAP_Y; y++)
		{
			for (int x = 0; x < BIGMAP_X; x++)
			{
				baseBigMap[y][x] = bigMap[y][x];
			}
		}

		//移動後プレイヤー位置設定
		baseBigMap[posY][posX] = 99;

		for (int y = 0; y < MAP_Y; y++)
		{
			for (int x = 0; x < MAP_X; x++)
			{
				map[y][x] = baseBigMap[scroll_Y + y][scroll_X + x];
			}
		}

		//消して描画
		system("cls");
		drowMap();

		//前回プレイヤー位置クリア


		std::cout << "scroll_Y " << scroll_Y;
		std::cout << "\nposY " << posY;

		//キー入力受付
		inputKey = _getch();

		movePlayer();
	}
}

//==================================================
//マップ描画関数
//==================================================
void drowMap()
{
	//マップの描画
	for (int y = 0; y < MAP_Y; y++)
	{
		for (int x = 0; x < MAP_X; x++)
		{
			switch (map[y][x])
			{
			case 1:
				std::cout << '=';
				break;
			case 2:
				std::cout << '-';
				break;
			case 3:
				std::cout << '|';
				break;
			case 99:
				std::cout << 'P';
				break;
			default:
				std::cout << ' ';
				break;
			}
		}

		std::cout << "\n";
	}
}

//==================================================
//プレイヤー移動関数
//==================================================
void movePlayer()
{
	//１つ前のプレイヤー位置保持
	prevPosY = posY;
	prevPosX = posX;

	//入力判定
	if (inputKey == 'w')
		posY -= 1;
	if (inputKey == 's')
		posY += 1;
	if (inputKey == 'a')
		posX -= 1;
	if (inputKey == 'd')
		posX += 1;

}


